import React, { useState } from "react";
import axios from "axios";

const RandomDog = () => {
  const [dogImage, setDogImage] = useState(""); // Í∞ïÏïÑÏßÄ ÏÇ¨ÏßÑ URL ÏÉÅÌÉú Í¥ÄÎ¶¨
  const [loadingImage, setLoadingImage] = useState(false); // Ïù¥ÎØ∏ÏßÄ Î°úÎî© ÏÉÅÌÉú Í¥ÄÎ¶¨
  const [breeds, setBreeds] = useState([]); // Í∞ïÏïÑÏßÄ ÌíàÏ¢Ö Î¶¨Ïä§Ìä∏ ÏÉÅÌÉú Í¥ÄÎ¶¨
  const [loadingBreeds, setLoadingBreeds] = useState(false); // ÌíàÏ¢Ö Î°úÎî© ÏÉÅÌÉú Í¥ÄÎ¶¨

  // ÎûúÎç§ Í∞ïÏïÑÏßÄ ÏÇ¨ÏßÑ Í∞ÄÏ†∏Ïò§Í∏∞
  const fetchRandomDog = async () => {
    setLoadingImage(true);
    try {
      const response = await axios.get(
        "https://dog.ceo/api/breeds/image/random"
      );
      setDogImage(response.data.message);
    } catch (error) {
      console.error("Error fetching random dog image:", error);
    } finally {
      setLoadingImage(false);
    }
  };

  // Í∞ïÏïÑÏßÄ ÌíàÏ¢Ö Î¶¨Ïä§Ìä∏ Í∞ÄÏ†∏Ïò§Í∏∞
  const fetchDogBreeds = async () => {
    setLoadingBreeds(true);
    try {
      const response = await axios.get("https://dog.ceo/api/breeds/list/all");
      const breedNames = Object.keys(response.data.message);
      setBreeds(breedNames);
    } catch (error) {
      console.error("Error fetching dog breeds:", error);
    } finally {
      setLoadingBreeds(false);
    }
  };

  return (
    <div style={{ textAlign: "center", marginTop: "20px" }}>
      <h1>üêï Random Dog Generator & Breed List üêï</h1>

      {/* ÎûúÎç§ Í∞ïÏïÑÏßÄ ÏÇ¨ÏßÑ Î≤ÑÌäº */}
      <button
        onClick={fetchRandomDog}
        style={{
          padding: "10px 20px",
          fontSize: "16px",
          cursor: "pointer",
          marginBottom: "20px",
          marginRight: "10px",
        }}
      >
        {loadingImage ? "Loading..." : "Get Random Dog"}
      </button>

      {/* Í∞ïÏïÑÏßÄ ÌíàÏ¢Ö Î¶¨Ïä§Ìä∏ Î≤ÑÌäº */}
      <button
        onClick={fetchDogBreeds}
        style={{
          padding: "10px 20px",
          fontSize: "16px",
          cursor: "pointer",
          marginBottom: "20px",
        }}
      >
        {loadingBreeds ? "Loading..." : "List All Breeds"}
      </button>

      {/* Í∞ïÏïÑÏßÄ ÏÇ¨ÏßÑ */}
      {dogImage && (
        <div>
          <img
            src={dogImage}
            alt="Random Dog"
            style={{
              width: "300px",
              height: "300px",
              objectFit: "cover",
              borderRadius: "10px",
              marginTop: "20px",
            }}
          />
        </div>
      )}

      {/* Í∞ïÏïÑÏßÄ ÌíàÏ¢Ö Î¶¨Ïä§Ìä∏ */}
      {breeds.length > 0 && (
        <div style={{ marginTop: "20px" }}>
          <h2>üêæ Dog Breeds üêæ</h2>
          <ul style={{ listStyle: "none", padding: 0 }}>
            {breeds.map((breed, index) => (
              <li key={index} style={{ marginBottom: "5px" }}>
                {breed}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default RandomDog;
