import React, { useState } from "react";
import axios from "axios";

const RandomDog = () => {
  const [dogImage, setDogImage] = useState(""); // Í∞ïÏïÑÏßÄ ÏÇ¨ÏßÑ URL ÏÉÅÌÉú Í¥ÄÎ¶¨
  const [loading, setLoading] = useState(false); // Î°úÎî© ÏÉÅÌÉú Í¥ÄÎ¶¨

  const fetchRandomDog = async () => {
    setLoading(true); // Î°úÎî© ÏãúÏûë
    try {
      const response = await axios.get(
        "https://dog.ceo/api/breeds/image/random"
      );
      setDogImage(response.data.message); // ÏÇ¨ÏßÑ URL ÏóÖÎç∞Ïù¥Ìä∏
    } catch (error) {
      console.error("Error fetching random dog image:", error);
    } finally {
      setLoading(false); // Î°úÎî© Ï¢ÖÎ£å
    }
  };

  return (
    <div style={{ textAlign: "center", marginTop: "20px" }}>
      <h1>üêï Random Dog Generator üêï</h1>
      <button
        onClick={fetchRandomDog}
        style={{
          padding: "10px 20px",
          fontSize: "16px",
          cursor: "pointer",
          marginBottom: "20px",
        }}
      >
        {loading ? "Loading..." : "Get Random Dog"}
      </button>
      {dogImage && (
        <div>
          <img
            src={dogImage}
            alt="Random Dog"
            style={{
              width: "300px",
              height: "300px",
              objectFit: "cover",
              borderRadius: "10px",
            }}
          />
        </div>
      )}
    </div>
  );
};

export default RandomDog;
