import React, { useState, useEffect } from "react";
import axios from "axios";

const RandomDog = () => {
  const [dogImage, setDogImage] = useState(""); // Í∞ïÏïÑÏßÄ ÏÇ¨ÏßÑ URL ÏÉÅÌÉú Í¥ÄÎ¶¨
  const [breeds, setBreeds] = useState([]); // Í∞ïÏïÑÏßÄ ÌíàÏ¢Ö Î¶¨Ïä§Ìä∏ ÏÉÅÌÉú Í¥ÄÎ¶¨
  const [loadingImage, setLoadingImage] = useState(false); // Ïù¥ÎØ∏ÏßÄ Î°úÎî© ÏÉÅÌÉú Í¥ÄÎ¶¨
  const [loadingBreeds, setLoadingBreeds] = useState(false); // ÌíàÏ¢Ö Î°úÎî© ÏÉÅÌÉú Í¥ÄÎ¶¨

  // Î™®Îì† ÌíàÏ¢Ö Î¶¨Ïä§Ìä∏ Í∞ÄÏ†∏Ïò§Í∏∞
  useEffect(() => {
    const fetchBreeds = async () => {
      setLoadingBreeds(true);
      try {
        const response = await axios.get("https://dog.ceo/api/breeds/list/all");
        const breedNames = Object.keys(response.data.message);
        setBreeds(breedNames);
      } catch (error) {
        console.error("Error fetching dog breeds:", error);
      } finally {
        setLoadingBreeds(false);
      }
    };

    fetchBreeds();
  }, []);

  // ÎûúÎç§ Í∞ïÏïÑÏßÄ ÏÇ¨ÏßÑ Í∞ÄÏ†∏Ïò§Í∏∞ (ÌíàÏ¢Ö ÏÑ†ÌÉù Ïãú)
  const fetchBreedImage = async (breed) => {
    setLoadingImage(true);
    try {
      const response = await axios.get(
        `https://dog.ceo/api/breed/${breed}/images/random`
      );
      setDogImage(response.data.message);
    } catch (error) {
      console.error(`Error fetching random image for breed ${breed}:`, error);
    } finally {
      setLoadingImage(false);
    }
  };

  return (
    <div style={{ textAlign: "center", marginTop: "20px" }}>
      <h1>üêï Random Dog Generator üêï</h1>

      {/* Í∞ïÏïÑÏßÄ ÏÇ¨ÏßÑ */}
      {dogImage && (
        <div>
          <img
            src={dogImage}
            alt="Random Dog"
            style={{
              width: "300px",
              height: "300px",
              objectFit: "cover",
              borderRadius: "10px",
              marginTop: "20px",
            }}
          />
        </div>
      )}

      {/* ÌíàÏ¢Ö Î¶¨Ïä§Ìä∏ */}
      <div style={{ marginTop: "20px" }}>
        <h2>üêæ Dog Breeds üêæ</h2>
        {loadingBreeds ? (
          <p>Loading breeds...</p>
        ) : (
          <ul
            style={{
              listStyle: "none",
              padding: 0,
              maxHeight: "200px",
              overflowY: "scroll",
            }}
          >
            {breeds.map((breed, index) => (
              <li
                key={index}
                style={{
                  marginBottom: "5px",
                  cursor: "pointer",
                  textDecoration: "underline",
                  color: "blue",
                }}
                onClick={() => fetchBreedImage(breed)}
              >
                {breed}
              </li>
            ))}
          </ul>
        )}
      </div>

      {/* Î°úÎî© ÏÉÅÌÉú ÌëúÏãú */}
      {loadingImage && <p>Loading image...</p>}
    </div>
  );
};

export default RandomDog;
