import React, { useState, useEffect } from "react";
import axios from "axios";

const RandomDog = () => {
  const [dogImage, setDogImage] = useState(""); // 강아지 사진 URL 상태 관리
  const [breeds, setBreeds] = useState([]); // 강아지 품종 리스트 상태 관리
  const [loadingImage, setLoadingImage] = useState(false); // 이미지 로딩 상태 관리
  const [loadingBreeds, setLoadingBreeds] = useState(false); // 품종 로딩 상태 관리

  // 모든 품종 리스트 가져오기
  useEffect(() => {
    const fetchBreeds = async () => {
      setLoadingBreeds(true);
      try {
        const response = await axios.get("https://dog.ceo/api/breeds/list/all");
        const breedNames = Object.keys(response.data.message);
        setBreeds(breedNames);
      } catch (error) {
        console.error("Error fetching dog breeds:", error);
      } finally {
        setLoadingBreeds(false);
      }
    };

    fetchBreeds();
  }, []);

  // 랜덤 강아지 사진 가져오기 (품종 선택 시)
  const fetchBreedImage = async (breed) => {
    setLoadingImage(true);
    try {
      const response = await axios.get(
        `https://dog.ceo/api/breed/${breed}/images/random`
      );
      setDogImage(response.data.message);
    } catch (error) {
      console.error(`Error fetching random image for breed ${breed}:`, error);
    } finally {
      setLoadingImage(false);
    }
  };

  return (
    <div
      style={{
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        height: "100vh",
        backgroundColor: "#f7f7f7",
        fontFamily: "Arial, sans-serif",
        padding: "20px",
      }}
    >
      <h1 style={{ color: "#333", marginBottom: "20px" }}>
        🐕 Random Dog Generator 🐕
      </h1>

      {/* 이미지 공간 고정 */}
      <div
        style={{
          width: "350px",
          height: "350px",
          border: "2px dashed #ccc",
          borderRadius: "10px",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          marginBottom: "20px",
          backgroundColor: "#fff",
          color: "#888",
        }}
      >
        {loadingImage ? (
          <p>Loading image...</p>
        ) : dogImage ? (
          <img
            src={dogImage}
            alt="Random Dog"
            style={{
              width: "100%",
              height: "100%",
              objectFit: "cover",
              borderRadius: "10px",
            }}
          />
        ) : (
          <p style={{ fontSize: "16px", textAlign: "center" }}>
            강아지 품종을 선택해주세요. <br /> 랜덤으로 사진을 가져와요.
          </p>
        )}
      </div>

      {/* 품종 리스트 */}
      <div
        style={{
          width: "400px",
          backgroundColor: "#fff",
          border: "1px solid #ccc",
          borderRadius: "10px",
          padding: "20px",
          boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
        }}
      >
        <h2
          style={{ color: "#555", marginBottom: "15px", textAlign: "center" }}
        >
          🐾 Dog Breeds 🐾
        </h2>
        {loadingBreeds ? (
          <p>Loading breeds...</p>
        ) : (
          <ul
            style={{
              listStyle: "none",
              padding: 0,
              maxHeight: "300px",
              overflowY: "auto",
              margin: 0,
            }}
          >
            {breeds.map((breed, index) => (
              <li
                key={index}
                style={{
                  marginBottom: "10px",
                  padding: "10px",
                  borderRadius: "5px",
                  backgroundColor: "#f1f1f1",
                  color: "#333",
                  cursor: "pointer",
                  textAlign: "center",
                  transition: "background-color 0.2s",
                }}
                onClick={() => fetchBreedImage(breed)}
                onMouseEnter={(e) => {
                  e.target.style.backgroundColor = "#e0e0e0";
                }}
                onMouseLeave={(e) => {
                  e.target.style.backgroundColor = "#f1f1f1";
                }}
              >
                {breed}
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default RandomDog;
