import React, { useState, useEffect } from "react";
import axios from "axios";

const RandomDog = () => {
  const [dogImage, setDogImage] = useState(""); // Í∞ïÏïÑÏßÄ ÏÇ¨ÏßÑ URL ÏÉÅÌÉú Í¥ÄÎ¶¨
  const [breeds, setBreeds] = useState([]); // Í∞ïÏïÑÏßÄ ÌíàÏ¢Ö Î¶¨Ïä§Ìä∏ ÏÉÅÌÉú Í¥ÄÎ¶¨
  const [loadingImage, setLoadingImage] = useState(false); // Ïù¥ÎØ∏ÏßÄ Î°úÎî© ÏÉÅÌÉú Í¥ÄÎ¶¨
  const [loadingBreeds, setLoadingBreeds] = useState(false); // ÌíàÏ¢Ö Î°úÎî© ÏÉÅÌÉú Í¥ÄÎ¶¨

  // Î™®Îì† ÌíàÏ¢Ö Î¶¨Ïä§Ìä∏ Í∞ÄÏ†∏Ïò§Í∏∞
  useEffect(() => {
    const fetchBreeds = async () => {
      setLoadingBreeds(true);
      try {
        const response = await axios.get("https://dog.ceo/api/breeds/list/all");
        const breedNames = Object.keys(response.data.message);
        setBreeds(breedNames);
      } catch (error) {
        console.error("Error fetching dog breeds:", error);
      } finally {
        setLoadingBreeds(false);
      }
    };

    fetchBreeds();
  }, []);

  // ÎûúÎç§ Í∞ïÏïÑÏßÄ ÏÇ¨ÏßÑ Í∞ÄÏ†∏Ïò§Í∏∞ (ÌíàÏ¢Ö ÏÑ†ÌÉù Ïãú)
  const fetchBreedImage = async (breed) => {
    setLoadingImage(true);
    try {
      const response = await axios.get(
        `https://dog.ceo/api/breed/${breed}/images/random`
      );
      setDogImage(response.data.message);
    } catch (error) {
      console.error(`Error fetching random image for breed ${breed}:`, error);
    } finally {
      setLoadingImage(false);
    }
  };

  return (
    <div
      style={{
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        height: "100vh",
        fontFamily: "Arial, sans-serif",
      }}
    >
      <h1>üêï Random Dog Generator & Breed List üêï</h1>

      {/* Ïù¥ÎØ∏ÏßÄ Í≥µÍ∞Ñ Í≥†Ï†ï */}
      <div
        style={{
          width: "300px",
          height: "300px",
          border: "2px dashed #ccc",
          borderRadius: "10px",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          marginBottom: "20px",
        }}
      >
        {loadingImage ? (
          <p>Loading image...</p>
        ) : (
          dogImage && (
            <img
              src={dogImage}
              alt="Random Dog"
              style={{
                width: "100%",
                height: "100%",
                objectFit: "cover",
                borderRadius: "10px",
              }}
            />
          )
        )}
      </div>

      {/* ÌíàÏ¢Ö Î¶¨Ïä§Ìä∏ */}
      <div style={{ maxWidth: "700px", textAlign: "center" }}>
        <h2>üêæ Dog Breeds üêæ</h2>
        {loadingBreeds ? (
          <p>Loading breeds...</p>
        ) : (
          <ul
            style={{
              listStyle: "none",
              padding: 0,
              maxHeight: "300px",
              overflowY: "scroll",
              border: "1px solid #ccc",
              borderRadius: "10px",
              padding: "10px",
            }}
          >
            {breeds.map((breed, index) => (
              <li
                key={index}
                style={{
                  marginBottom: "10px",
                  cursor: "pointer",
                  textDecoration: "underline",
                  color: "blue",
                  position: "relative",
                }}
                onClick={() => fetchBreedImage(breed)}
                onMouseEnter={(e) => {
                  const tooltip = document.createElement("div");
                  tooltip.className = "tooltip";
                  tooltip.textContent = `API: /breed/${breed}/images/random
Request: GET
Response: { message: "Image URL", status: "success" }`;
                  tooltip.style.position = "absolute";
                  tooltip.style.top = "50%";
                  tooltip.style.left = "105%";
                  tooltip.style.transform = "translateY(-50%)";
                  tooltip.style.background = "#000";
                  tooltip.style.color = "#fff";
                  tooltip.style.padding = "5px 10px";
                  tooltip.style.borderRadius = "5px";
                  tooltip.style.whiteSpace = "pre-wrap";
                  tooltip.style.fontSize = "12px";
                  e.target.appendChild(tooltip);
                }}
                onMouseLeave={(e) => {
                  const tooltip = e.target.querySelector(".tooltip");
                  if (tooltip) e.target.removeChild(tooltip);
                }}
              >
                {breed}
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default RandomDog;
