import React, { useState, useEffect } from "react";
import axios from "axios";

const RandomDog = () => {
  const [dogImage, setDogImage] = useState(""); // Í∞ïÏïÑÏßÄ ÏÇ¨ÏßÑ URL ÏÉÅÌÉú Í¥ÄÎ¶¨
  const [breeds, setBreeds] = useState({}); // Í∞ïÏïÑÏßÄ ÌíàÏ¢Ö Î¶¨Ïä§Ìä∏ ÏÉÅÌÉú Í¥ÄÎ¶¨ (ÏïåÌååÎ≤≥Î≥ÑÎ°ú Í∑∏Î£πÌôî)
  const [selectedLetter, setSelectedLetter] = useState(""); // ÏÑ†ÌÉùÎêú ÏïåÌååÎ≤≥
  const [loadingImage, setLoadingImage] = useState(false); // Ïù¥ÎØ∏ÏßÄ Î°úÎî© ÏÉÅÌÉú Í¥ÄÎ¶¨
  const [loadingBreeds, setLoadingBreeds] = useState(false); // ÌíàÏ¢Ö Î°úÎî© ÏÉÅÌÉú Í¥ÄÎ¶¨

  // Î™®Îì† ÌíàÏ¢Ö Î¶¨Ïä§Ìä∏ Í∞ÄÏ†∏Ïò§Í∏∞
  useEffect(() => {
    const fetchBreeds = async () => {
      setLoadingBreeds(true);
      try {
        const response = await axios.get("https://dog.ceo/api/breeds/list/all");
        const breedNames = Object.keys(response.data.message);
        // ÌíàÏ¢ÖÏùÑ ÏïåÌååÎ≤≥ Í∏∞Ï§ÄÏúºÎ°ú Í∑∏Î£πÌôî
        const groupedBreeds = breedNames.reduce((acc, breed) => {
          const letter = breed[0].toUpperCase();
          if (!acc[letter]) acc[letter] = [];
          acc[letter].push(breed);
          return acc;
        }, {});
        setBreeds(groupedBreeds);
      } catch (error) {
        console.error("ÎåïÎåïÏù¥ ÌíàÏ¢Ö Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:", error);
      } finally {
        setLoadingBreeds(false);
      }
    };

    fetchBreeds();
  }, []);

  // ÎûúÎç§ Í∞ïÏïÑÏßÄ ÏÇ¨ÏßÑ Í∞ÄÏ†∏Ïò§Í∏∞ (ÌíàÏ¢Ö ÏÑ†ÌÉù Ïãú)
  const fetchBreedImage = async (breed) => {
    setLoadingImage(true);
    try {
      const response = await axios.get(
        `https://dog.ceo/api/breed/${breed}/images/random`
      );
      setDogImage(response.data.message);
    } catch (error) {
      console.error(`Error fetching random image for breed ${breed}:`, error);
    } finally {
      setLoadingImage(false);
    }
  };

  return (
    <div
      style={{
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        height: "100vh",
        backgroundColor: "#f7f7f7",
        fontFamily: "Arial, sans-serif",
        padding: "20px",
      }}
    >
      {/* Ï†úÎ™© */}
      <h1 style={{ color: "#333", marginBottom: "20px" }}>
        üêï ÎûúÎç§ ÎåïÎåïÏù¥ ÏÉùÏÑ±Í∏∞ üêï
      </h1>

      <div
        style={{
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          width: "100%",
        }}
      >
        {/* Ïù¥ÎØ∏ÏßÄ Î∞ïÏä§ */}
        <div
          style={{
            width: "350px",
            height: "350px",
            border: "2px dashed #ccc",
            borderRadius: "10px",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            marginRight: "20px",
            backgroundColor: "#fff",
            color: "#888",
          }}
        >
          {loadingImage ? (
            <p>Loading image...</p>
          ) : dogImage ? (
            <img
              src={dogImage}
              alt="Random Dog"
              style={{
                width: "100%",
                height: "100%",
                objectFit: "cover",
                borderRadius: "10px",
              }}
            />
          ) : (
            <p style={{ fontSize: "16px", textAlign: "center" }}>
              Í∞ïÏïÑÏßÄ ÌíàÏ¢ÖÏùÑ ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî <br /> ÎûúÎç§ÏúºÎ°ú ÏÇ¨ÏßÑÏùÑ Í∞ÄÏ†∏ÏôÄÏöî
            </p>
          )}
        </div>

        {/* ÌíàÏ¢Ö Î¶¨Ïä§Ìä∏ */}
        <div
          style={{
            display: "flex",
            flexDirection: "row",
            width: "450px",
            height: "350px",
            backgroundColor: "#fff",
            border: "1px solid #ccc",
            borderRadius: "10px",
            boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
          }}
        >
          {/* ÏïåÌååÎ≤≥ Î¶¨Ïä§Ìä∏ */}
          <div
            style={{
              width: "50px",
              borderRight: "1px solid #ccc",
              display: "flex",
              flexDirection: "column",
              alignItems: "center",
              justifyContent: "start",
              paddingTop: "10px",
              backgroundColor: "#f9f9f9",
              overflowY: "scroll", // Ïä§ÌÅ¨Î°§ Í∞ÄÎä•
              maxHeight: "100%",
              scrollbarWidth: "none", // FirefoxÏóêÏÑú Ïä§ÌÅ¨Î°§Î∞î Ï†úÍ±∞
              msOverflowStyle: "none", // IEÏóêÏÑú Ïä§ÌÅ¨Î°§Î∞î Ï†úÍ±∞
            }}
          >
            <style>
              {`
                /* Chrome, Edge, Safari Ïä§ÌÅ¨Î°§Î∞î Ï†úÍ±∞ */
                div::-webkit-scrollbar {
                  display: none;
                }
              `}
            </style>
            {Object.keys(breeds).map((letter) => (
              <div
                key={letter}
                style={{
                  padding: "10px",
                  cursor: "pointer",
                  fontWeight: letter === selectedLetter ? "bold" : "normal",
                  color: letter === selectedLetter ? "#333" : "#888",
                  transition: "color 0.2s",
                }}
                onClick={() => setSelectedLetter(letter)}
              >
                {letter}
              </div>
            ))}
          </div>

          {/* ÏÑ†ÌÉùÎêú ÏïåÌååÎ≤≥Ïùò ÌíàÏ¢Ö Î¶¨Ïä§Ìä∏ */}
          <div
            style={{
              flex: 1,
              overflowY: "auto",
              padding: "10px",
              backgroundColor: "#fff",
            }}
          >
            <h3
              style={{
                textAlign: "center",
                color: "#555",
                marginBottom: "10px",
              }}
            >
              {selectedLetter
                ? `${selectedLetter}Î°ú ÏãúÏûëÌïòÎäî ÌíàÏ¢Ö`
                : "ÏïåÌååÎ≤≥ÏùÑ ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî"}
            </h3>
            {selectedLetter && breeds[selectedLetter] ? (
              <ul
                style={{
                  listStyle: "none",
                  padding: 0,
                  margin: 0,
                }}
              >
                {breeds[selectedLetter].map((breed) => (
                  <li
                    key={breed}
                    style={{
                      marginBottom: "10px",
                      padding: "10px",
                      borderRadius: "5px",
                      backgroundColor: "#f1f1f1",
                      color: "#333",
                      cursor: "pointer",
                      textAlign: "center",
                      transition: "background-color 0.2s",
                    }}
                    onClick={() => fetchBreedImage(breed)}
                    onMouseEnter={(e) => {
                      e.target.style.backgroundColor = "#e0e0e0";
                    }}
                    onMouseLeave={(e) => {
                      e.target.style.backgroundColor = "#f1f1f1";
                    }}
                  >
                    {breed}
                  </li>
                ))}
              </ul>
            ) : (
              <p style={{ textAlign: "center", color: "#888" }}>ÌíàÏ¢Ö ÏóÜÏùå</p>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default RandomDog;
